mapper.select:

select 

from IVB_T_BLOCK_HARVEST_SCHEDULES_AMOUNT
where FARM_ID = #{farmId,jdbcType=VARCHAR}
  and AREA_ID = #{areaId,jdbcType=VARCHAR}
  and BLOCK_ID = #{blockId,jdbcType=VARCHAR}

mapper.delete:

delete from IVB_T_BLOCK_HARVEST_SCHEDULES_AMOUNT
where FARM_ID = #{farmId,jdbcType=VARCHAR}
  and AREA_ID = #{areaId,jdbcType=VARCHAR}
  and BLOCK_ID = #{blockId,jdbcType=VARCHAR}
