mapper.select:

select 

from IVB_T_PRODUCT
where FARM_ID = #{farmId,jdbcType=VARCHAR}
  and AREA_ID = #{areaId,jdbcType=VARCHAR}
  and BLOCK_ID = #{blockId,jdbcType=VARCHAR}
  and LINE_ID = #{lineId,jdbcType=VARCHAR}
  and COLUMN_ID = #{columnId,jdbcType=VARCHAR}
  and PREVIOUS_ROUND = #{previousRound,jdbcType=BIT}

mapper.delete:

delete from IVB_T_PRODUCT
where FARM_ID = #{farmId,jdbcType=VARCHAR}
  and AREA_ID = #{areaId,jdbcType=VARCHAR}
  and BLOCK_ID = #{blockId,jdbcType=VARCHAR}
  and LINE_ID = #{lineId,jdbcType=VARCHAR}
  and COLUMN_ID = #{columnId,jdbcType=VARCHAR}
  and PREVIOUS_ROUND = #{previousRound,jdbcType=BIT}

mapper.update:

update IVB_T_PRODUCT


    CULTIVATION_START_DATE = #{cultivationStartDate,jdbcType=DATE},


    PLANTING_DATE = #{plantingDate,jdbcType=DATE},


    FLOWERING_DATE = #{floweringDate,jdbcType=DATE},


    BAG_CLOSING_DATE = #{bagClosingDate,jdbcType=DATE},


    HARVEST_DATE = #{harvestDate,jdbcType=DATE},


    SHIPPING_DATE = #{shippingDate,jdbcType=DATE},


    INFOR_ID = #{inforId,jdbcType=VARCHAR},


    INFOR_DISPLAY_START_DATE = #{inforDisplayStartDate,jdbcType=DATE},


    INFOR_DISPLAY_END_DATE = #{inforDisplayEndDate,jdbcType=DATE},


    CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR},


    UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR},

    LAST_UPDATE_DATE = NOW(),

    DELETE_FLAG = #{deleteFlag,jdbcType=BIT},


where FARM_ID = #{farmId,jdbcType=VARCHAR}
  and AREA_ID = #{areaId,jdbcType=VARCHAR}
  and BLOCK_ID = #{blockId,jdbcType=VARCHAR}
  and LINE_ID = #{lineId,jdbcType=VARCHAR}
  and COLUMN_ID = #{columnId,jdbcType=VARCHAR}
  and PREVIOUS_ROUND = #{previousRound,jdbcType=BIT}


update IVB_T_PRODUCT
set CULTIVATION_START_DATE = #{cultivationStartDate,jdbcType=DATE},
  PLANTING_DATE = #{plantingDate,jdbcType=DATE},
  FLOWERING_DATE = #{floweringDate,jdbcType=DATE},
  BAG_CLOSING_DATE = #{bagClosingDate,jdbcType=DATE},
  HARVEST_DATE = #{harvestDate,jdbcType=DATE},
  SHIPPING_DATE = #{shippingDate,jdbcType=DATE},
  INFOR_ID = #{inforId,jdbcType=VARCHAR},
  INFOR_DISPLAY_START_DATE = #{inforDisplayStartDate,jdbcType=DATE},
  INFOR_DISPLAY_END_DATE = #{inforDisplayEndDate,jdbcType=DATE},
  UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR},
  LAST_UPDATE_DATE = NOW(),
  DELETE_FLAG = #{deleteFlag,jdbcType=BIT}
where FARM_ID = #{farmId,jdbcType=VARCHAR}
  and AREA_ID = #{areaId,jdbcType=VARCHAR}
  and BLOCK_ID = #{blockId,jdbcType=VARCHAR}
  and LINE_ID = #{lineId,jdbcType=VARCHAR}
  and COLUMN_ID = #{columnId,jdbcType=VARCHAR}
  and PREVIOUS_ROUND = #{previousRound,jdbcType=BIT}

